import React, { useState } from 'react';

const axios = require('axios');

export default function Contact() {

  const [state, setState] = useState({
    name: '',
    email: '',
    message: ''
  })

  const handleSubmit = e => {
    e.preventDefault();
    axios({
      method: "POST", 
      url:"http://localhost:3002/send", 
      data:  this.state
    }).then((response)=>{
      if (response.data.status === 'success') {
        alert("Message Sent."); 
        this.resetForm()
      } else if (response.data.status === 'fail') {
        alert("Message failed to send.")
      }
    })
  }

  const onNameChange = e => {
    this.setState({name: e.target.value})
  }

  const onEmailChange = e => {
    this.setState({email: e.target.value})
  }

  const onMessageChange = e => {
    this.setState({message: e.target.value})
  }

  return(

  <form id="contact-form" onSubmit={handleSubmit.bind(this)} method="POST">
    <div className="form-group">
        <label htmlFor="name">Name</label>
        <input type="text" className="form-control" id="name" value={state.name} onChange={onNameChange.bind(this)} />
    </div>
    <div className="form-group">
        <label htmlFor="exampleInputEmail1">Email address</label>
        <input type="email" className="form-control" id="email" aria-describedby="emailHelp" value={ttate.email} onChange={this.onEmailChange.bind(this)} />
    </div>
    <div className="form-group">
        <label htmlFor="message">Message</label>
        <textarea className="form-control" rows="5" id="message" value={state.message} onChange={onMessageChange.bind(this)} />
    </div>
    <button type="submit" className="btn btn-primary">Submit</button>
  </form>

  );

}
