import React, { useState } from 'react';

import "../components/body.scss";

const axios = require('axios');

export default function Contact() {

  const [state, setState] = useState({
    name: '',
    email: '',
    message: ''
  })

  const handleSubmit = e => {
    e.preventDefault();
    axios({
      method: "POST", 
      url:"http://localhost:3002/send", 
      data: state
    }).then((response)=>{
      if (response.data.status === 'success') {
        alert("Message Sent."); 
        this.resetForm()
      } else if (response.data.status === 'fail') {
        alert("Message failed to send.")
      }
    })
  }

  const onNameChange = e => {
    this.setState({name: e.target.value})
  }

  const onEmailChange = e => {
    this.setState({email: e.target.value})
  }

  const onMessageChange = e => {
    this.setState({message: e.target.value})
  }

  return(

  <form className = "contact" onSubmit={handleSubmit.bind(this)} method="POST">
    <h3 id = "contact" > Contact </h3>
    <input type="text" className="form-control" id="name" placeholder = "name" value={state.name} onChange={onNameChange.bind(this)} />
    <input type="email" className="form-control" id="email" placeholder = "email" value={state.email} onChange={onEmailChange.bind(this)} />
    <textarea className="form-control" id="message" placeholder = "message" value={state.message} onChange={onMessageChange.bind(this)} />
    <button type="submit" className="btn btn-primary">Submit</button>
  </form>

  );

}
