import React, { useRef } from "react";
import "../components/Menu.scss";

export default function Menu() {
  const itemsRef = useRef([]);

  const onClick = () => {
    window.scrollTo({ behavior: 'smooth', top: itemsRef.current.offsetTop })
  };

  return (
      <li className = "Menu"> 
        <h2 className = "Menu--item" onClick = {onClick}> About Me </h2>
        <h2 className = "Menu--item" onClick = {onClick}> Projects </h2>
      </li>
  )
}
